  // }).catch(err => console.log(err))
  // }


  componentDidMount() {
    axios.get(baseUrl)
      .then(res => {
        this.setState({ post:res.data });
      })
      // .catch (err) => {
      //   console.log(err);
      // }
  };

  render() {
    return(
      <div>
        {this.prop.title}
        <br/>
        {this.state.description}
      </div>
    )
  }

}

export default Main;
