import React from 'react';
import axios from 'axios';

let baseUrl = 'http://localhost:3005';

class Main extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      posts: []
    }
  }

  fetchPosts = () => {
  fetch(`${baseUrl}`)
  .then(data => data.json())
  .then(jData => {
    this.setState({
      posts: jData
    })
  }).catch(err => console.log(err))
  }


  componentDidMount() {
    axios.get(baseUrl)
      .then(res => {
        this.setState({ list:res.data });
      })
  };

  render() {
    return(
      <div>
        {this.state.title}
        <br/>
        {this.state.description}
      </div>
    )
  }

}

export default Main;
