import React from 'react';

class Form extends React.Component {
  constructor() {
    super()
    this.state = {
      title: '',
      description: '',
      completed: null,
      id: null
    }
  };

  handleSubmit = (event) => {
    event.preventDefault()
    this.props.handleCreate(this.state)
  };

  componentDidMount() {
    this.setState({
      title: this.props.inputs.title,
      description: this.props.inputs.description,
      completed: this.props.inputs.completed,
      id: this.props.inputs.id
    })
  }

  render() {
    return(
      <form onSubmit={this.handleSubmit}>
        <input type="text" placeholder="Title" id="title" value={this.state.title} />
        <textarea placeholder="Description" id="description" value={this.state.description}> </textarea>
        Completed:
        <input type="checkbox" id="completed" value={this.state.completed} />
        <input type="submit" value="Create" />
      </form>
    )
  }
}

export default Form;
