import React from 'react';
import axios from 'axios';
import Post from './Post.js';

let baseUrl = 'http://localhost:3005';

class Main extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      posts: []
    }
  }

  fetchPosts = () => {
  fetch(`${baseUrl}`)
  .then(data => data.json())
  .then(jData => {
    this.setState({
      posts: jData
    })
  }).catch(err => console.log(err))
  }

  //
