  //     })
  //     // .catch (err) => {
  //     //   console.log(err);
  //     // }
  // };

  componentDidMount() {
    this.fetchPosts()
    console.log();
  }

  render() {
    return(
      <div>
      {this.state.posts.map((postData) => {
        <Post
          key={postData.id}
          postData={postData}
        />
        })}
      </div>
    )
  }

}

export default Main;
