import React from 'react';


class Post extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isChecked: props.isChecked || false
    }
  }


  handleCompleted() {
    this.setState({
      isChecked: !this.state.isChecked
    })
  }

  render() {
    return(
    <article>
      <div className="item-outer-container">
        <h2>{this.props.postData.title}</h2>
      <div className="item-inner-container">
      <div>
        <p>{this.props.postData.description}</p>
        <input type="checkbox"
        value={this.state.isChecked}
        onChange={this.handleCompleted}

        />
      </div>

      <div className="edit-delete-container">
        <ul>
          <li
          onClick={() =>
            {this.props.handleView('editItem', this.props.postData)
          }}
          >Edit</li>
          <li
          onClick={() =>
            {this.props.handleDelete(this.props.postData._id)}}
            >Delete</li>
        </ul>
      </div>
      </div>
      </div>
    </article>
    )
  }
}

export default Post;
